import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Alert, Card, Spinner } from 'react-bootstrap';
import { authAPI } from '../services/api';

const Interests = () => {
  const [interests, setInterests] = useState([]);
  const [selectedInterests, setSelectedInterests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const interestOptions = [
    'Hill Station',
    'Beach',
    'Adventure',
    'Heritage',
    'Spiritual',
    'Wildlife',
    'Cultural',
    'Food',
    'Photography',
    'Shopping',
    'Religious',
    'Historical',
    'Nature',
    'Sports',
    'Art'
  ];

  useEffect(() => {
    const username = localStorage.getItem('username');
    if (username) {
      fetchUserInterests(username);
    }
  }, []);

  const fetchUserInterests = async (username) => {
    setLoading(true);
    setError(null);
    
    try {
      const response = await authAPI.getUserInterests(username);
      setInterests(response.data.interests || []);
      setSelectedInterests(response.data.interests || []);
    } catch (err) {
      setError('Failed to fetch your interests');
    } finally {
      setLoading(false);
    }
  };

  const handleInterestChange = (interest) => {
    setSelectedInterests(prev => {
      if (prev.includes(interest)) {
        return prev.filter(i => i !== interest);
      } else {
        return [...prev, interest];
      }
    });
  };

  const handleSave = async () => {
    const username = localStorage.getItem('username');
    if (!username) {
      setError('Please log in to update your interests');
      return;
    }

    setSaving(true);
    setError(null);
    setSuccess(false);

    try {
      await authAPI.updateUserInterests(username, selectedInterests);
      setInterests(selectedInterests);
      setSuccess(true);
      setTimeout(() => setSuccess(false), 3000);
    } catch (err) {
      setError('Failed to update your interests');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div>
        <div className="page-header">
          <Container>
            <h1>My Interests</h1>
            <p>Loading your preferences...</p>
          </Container>
        </div>
        <Container className="py-5">
          <Row className="justify-content-center">
            <Col md={6} className="text-center">
              <Spinner animation="border" variant="primary" />
              <p className="mt-3">Loading your interests...</p>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }

  return (
    <div>
      <div className="page-header">
        <Container>
          <h1>My Interests</h1>
          <p>Update your preferences to get better recommendations</p>
        </Container>
      </div>

      <Container className="py-5">
        <Row className="justify-content-center">
          <Col lg={8}>
            <Card>
              <Card.Body className="p-4">
                <h5 className="card-title mb-4">Select Your Interests</h5>
                <p className="text-muted mb-4">
                  Choose the types of destinations and activities that interest you. 
                  This helps us provide more personalized recommendations.
                </p>

                {error && (
                  <Alert variant="danger" className="mb-4">
                    {error}
                  </Alert>
                )}

                {success && (
                  <Alert variant="success" className="mb-4">
                    Your interests have been updated successfully!
                  </Alert>
                )}

                <Form.Group className="mb-4">
                  <div className="row g-3">
                    {interestOptions.map((interest, index) => (
                      <Col md={4} sm={6} key={index}>
                        <Form.Check
                          type="checkbox"
                          id={`interest-${index}`}
                          label={interest}
                          checked={selectedInterests.includes(interest)}
                          onChange={() => handleInterestChange(interest)}
                          disabled={saving}
                        />
                      </Col>
                    ))}
                  </div>
                </Form.Group>

                <div className="d-flex justify-content-between align-items-center">
                  <div>
                    <small className="text-muted">
                      {selectedInterests.length} interest{selectedInterests.length !== 1 ? 's' : ''} selected
                    </small>
                  </div>
                  <Button
                    variant="primary"
                    onClick={handleSave}
                    disabled={saving}
                  >
                    {saving ? (
                      <>
                        <Spinner animation="border" size="sm" className="me-2" />
                        Saving...
                      </>
                    ) : (
                      'Save Interests'
                    )}
                  </Button>
                </div>
              </Card.Body>
            </Card>

            {/* Current Interests Display */}
            {interests.length > 0 && (
              <Card className="mt-4">
                <Card.Body>
                  <h6 className="card-title">Your Current Interests</h6>
                  <div className="d-flex flex-wrap gap-2">
                    {interests.map((interest, index) => (
                      <span key={index} className="badge bg-primary">
                        {interest}
                      </span>
                    ))}
                  </div>
                </Card.Body>
              </Card>
            )}

            {/* Benefits Section */}
            <Card className="mt-4">
              <Card.Body>
                <h6 className="card-title">Why Update Your Interests?</h6>
                <Row className="g-3">
                  <Col md={4}>
                    <div className="text-center">
                      <div className="fs-1 mb-2">ðŸŽ¯</div>
                      <h6>Personalized Recommendations</h6>
                      <p className="small text-muted">
                        Get destinations that match your preferences
                      </p>
                    </div>
                  </Col>
                  <Col md={4}>
                    <div className="text-center">
                      <div className="fs-1 mb-2">âš¡</div>
                      <h6>Faster Discovery</h6>
                      <p className="small text-muted">
                        Find perfect destinations quickly
                      </p>
                    </div>
                  </Col>
                  <Col md={4}>
                    <div className="text-center">
                      <div className="fs-1 mb-2">ðŸ“Š</div>
                      <h6>Better Analysis</h6>
                      <p className="small text-muted">
                        Get insights tailored to your interests
                      </p>
                    </div>
                  </Col>
                </Row>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Interests;
