import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Alert, Spinner, Tabs, Tab } from 'react-bootstrap';
import { dataAPI, aiAPI } from '../services/api';
import CompareCard from '../components/CompareCard';

const Compare = () => {
  const [states, setStates] = useState([]);
  const [cities1, setCities1] = useState([]);
  const [cities2, setCities2] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [comparisonData, setComparisonData] = useState(null);
  const [activeTab, setActiveTab] = useState('states');

  // State comparison
  const [state1, setState1] = useState('');
  const [state2, setState2] = useState('');

  // City comparison
  const [cityState1, setCityState1] = useState('');
  const [cityState2, setCityState2] = useState('');
  const [city1, setCity1] = useState('');
  const [city2, setCity2] = useState('');

  useEffect(() => {
    fetchStates();
  }, []);

  useEffect(() => {
    if (cityState1) {
      fetchCities(cityState1, 'cities1');
    }
  }, [cityState1]);

  useEffect(() => {
    if (cityState2) {
      fetchCities(cityState2, 'cities2');
    }
  }, [cityState2]);

  const fetchStates = async () => {
    try {
      const response = await dataAPI.getStates();
      setStates(response.data);
    } catch (err) {
      setError('Failed to fetch states');
    }
  };

  const fetchCities = async (stateName, setter) => {
    try {
      const response = await dataAPI.getStateCities(stateName);
      if (setter === 'cities1') {
        setCities1(response.data);
      } else {
        setCities2(response.data);
      }
    } catch (err) {
      setError('Failed to fetch cities');
    }
  };

  const handleStateCompare = async () => {
    if (!state1 || !state2) {
      setError('Please select both states');
      return;
    }

    setLoading(true);
    setError(null);
    setComparisonData(null);

    try {
      const response = await aiAPI.compareStates(state1, state2);
      setComparisonData({
        type: 'states',
        data: response.data
      });
    } catch (err) {
      setError('Failed to compare states');
    } finally {
      setLoading(false);
    }
  };

  const handleCityCompare = async () => {
    if (!cityState1 || !city1 || !cityState2 || !city2) {
      setError('Please select both cities');
      return;
    }

    setLoading(true);
    setError(null);
    setComparisonData(null);

    try {
      const response = await aiAPI.compareCities(cityState1, city1, cityState2, city2);
      setComparisonData({
        type: 'cities',
        data: response.data
      });
    } catch (err) {
      setError('Failed to compare cities');
    } finally {
      setLoading(false);
    }
  };

  const renderComparisonChart = () => {
    if (!comparisonData || !comparisonData.data) return null;

    return (
      <div className="card">
        <div className="card-body">
          <h6 className="card-title">Comparison Chart</h6>
          <div className="chart-container">
            {/* Chart would be implemented here with Chart.js */}
            <div className="text-center py-5">
              <div className="fs-1 text-muted">📊</div>
              <p className="text-muted">Comparison visualization would appear here</p>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div>
      <div className="page-header">
        <Container>
          <h1>Compare Destinations</h1>
          <p>Compare states and cities side by side</p>
        </Container>
      </div>

      <Container className="py-5">
        <Row className="mb-5">
          <Col lg={10} className="mx-auto">
            <Tabs
              activeKey={activeTab}
              onSelect={(k) => setActiveTab(k)}
              className="mb-4"
            >
              <Tab eventKey="states" title="State Comparison">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title mb-4">Compare States</h5>
                    <Row className="g-3">
                      <Col md={5}>
                        <Form.Group>
                          <Form.Label>First State</Form.Label>
                          <Form.Select
                            value={state1}
                            onChange={(e) => setState1(e.target.value)}
                            disabled={loading}
                          >
                            <option value="">Choose first state...</option>
                            {states.map((state, index) => (
                              <option key={index} value={state}>
                                {state}
                              </option>
                            ))}
                          </Form.Select>
                        </Form.Group>
                      </Col>
                      
                      <Col md={2} className="d-flex align-items-end">
                        <div className="text-center w-100">
                          <span className="text-muted">VS</span>
                        </div>
                      </Col>
                      
                      <Col md={5}>
                        <Form.Group>
                          <Form.Label>Second State</Form.Label>
                          <Form.Select
                            value={state2}
                            onChange={(e) => setState2(e.target.value)}
                            disabled={loading}
                          >
                            <option value="">Choose second state...</option>
                            {states.map((state, index) => (
                              <option key={index} value={state}>
                                {state}
                              </option>
                            ))}
                          </Form.Select>
                        </Form.Group>
                      </Col>
                    </Row>
                    
                    <div className="text-center mt-4">
                      <Button 
                        variant="primary" 
                        size="lg" 
                        onClick={handleStateCompare}
                        disabled={!state1 || !state2 || loading}
                      >
                        {loading ? (
                          <>
                            <Spinner animation="border" size="sm" className="me-2" />
                            Comparing...
                          </>
                        ) : (
                          'Compare States'
                        )}
                      </Button>
                    </div>
                  </div>
                </div>
              </Tab>

              <Tab eventKey="cities" title="City Comparison">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title mb-4">Compare Cities</h5>
                    <Row className="g-3">
                      <Col md={5}>
                        <Form.Group>
                          <Form.Label>First City</Form.Label>
                          <Form.Select
                            value={cityState1}
                            onChange={(e) => {
                              setCityState1(e.target.value);
                              setCity1('');
                            }}
                            disabled={loading}
                          >
                            <option value="">Choose state...</option>
                            {states.map((state, index) => (
                              <option key={index} value={state}>
                                {state}
                              </option>
                            ))}
                          </Form.Select>
                          <Form.Select
                            value={city1}
                            onChange={(e) => setCity1(e.target.value)}
                            disabled={!cityState1 || loading}
                            className="mt-2"
                          >
                            <option value="">Choose city...</option>
                            {cities1.map((city, index) => (
                              <option key={index} value={city}>
                                {city}
                              </option>
                            ))}
                          </Form.Select>
                        </Form.Group>
                      </Col>
                      
                      <Col md={2} className="d-flex align-items-end">
                        <div className="text-center w-100">
                          <span className="text-muted">VS</span>
                        </div>
                      </Col>
                      
                      <Col md={5}>
                        <Form.Group>
                          <Form.Label>Second City</Form.Label>
                          <Form.Select
                            value={cityState2}
                            onChange={(e) => {
                              setCityState2(e.target.value);
                              setCity2('');
                            }}
                            disabled={loading}
                          >
                            <option value="">Choose state...</option>
                            {states.map((state, index) => (
                              <option key={index} value={state}>
                                {state}
                              </option>
                            ))}
                          </Form.Select>
                          <Form.Select
                            value={city2}
                            onChange={(e) => setCity2(e.target.value)}
                            disabled={!cityState2 || loading}
                            className="mt-2"
                          >
                            <option value="">Choose city...</option>
                            {cities2.map((city, index) => (
                              <option key={index} value={city}>
                                {city}
                              </option>
                            ))}
                          </Form.Select>
                        </Form.Group>
                      </Col>
                    </Row>
                    
                    <div className="text-center mt-4">
                      <Button 
                        variant="primary" 
                        size="lg" 
                        onClick={handleCityCompare}
                        disabled={!cityState1 || !city1 || !cityState2 || !city2 || loading}
                      >
                        {loading ? (
                          <>
                            <Spinner animation="border" size="sm" className="me-2" />
                            Comparing...
                          </>
                        ) : (
                          'Compare Cities'
                        )}
                      </Button>
                    </div>
                  </div>
                </div>
              </Tab>
            </Tabs>
          </Col>
        </Row>

        {/* Error Alert */}
        {error && (
          <Row className="mb-4">
            <Col>
              <Alert variant="danger">{error}</Alert>
            </Col>
          </Row>
        )}

        {/* Comparison Results */}
        {comparisonData && comparisonData.data && (
          <Row className="g-4">
            <Col lg={6}>
              <CompareCard 
                data={comparisonData.data.state1_data}
                title={comparisonData.data.state1_data.name || 'First Destination'}
                type={activeTab}
              />
            </Col>
            
            <Col lg={6}>
              <CompareCard 
                data={comparisonData.data.state2_data}
                title={comparisonData.data.state2_data.name || 'Second Destination'}
                type={activeTab}
              />
            </Col>
            
            <Col lg={12}>
              {renderComparisonChart()}
            </Col>
          </Row>
        )}
      </Container>
    </div>
  );
};

export default Compare;
